// Base topic for Holding Registers
const baseTopicHR = "Modbus Device/HR";

// Get the array from msg.payload (expecting [123, 456, 789, ...])
const hrValues = msg.payload;

// Validate
if (!Array.isArray(hrValues)) {
    node.error("Expected an array for Holding Register values.");
    return null;
}

// Create and send messages for each HR value
hrValues.forEach((value, index) => {
    node.send({
        topic: `${baseTopicHR}${index}`,
        payload: value.toString()
    });
});

return null;

// Assume you already have values saved
const coilValues = context.get("coilValues") || Array(10).fill(false); // 10 coils
const registerValues = context.get("registerValues") || Array(11).fill(0); // 11 holding registers

// Extract topic and payload
const topic = msg.topic; // e.g., "ModbusDevice/CO3" or "ModbusDevice/HR5"
const newValue = msg.payload;

// Parse register or coil index
const match = topic.match(/(HR|CO)(\d+)/);

if (!match) {
    node.warn("Invalid topic format. Use HR<number> or CO<number>");
    return null;
}

const type = match[1]; // 'HR' or 'CO'
const index = parseInt(match[2], 10);

// Safety checks
if (type === "HR") {
    const parsedVal = parseInt(newValue, 10);
    if (isNaN(parsedVal) || index < 0 || index >= registerValues.length) {
        node.warn("Invalid HR value or index");
        return null;
    }

    registerValues[index] = parsedVal;

    // Save to context
    context.set("registerValues", registerValues);

    // Send modbus write message for HR
    msg.payload = {
        value: registerValues,
        fc: 16,
        unitid: 1,
        address: 0,
        quantity: registerValues.length
    };

} else if (type === "CO") {
    const boolVal = (newValue === true || newValue === "true" || newValue === 1 || newValue === "1");
    if (index < 0 || index >= coilValues.length) {
        node.warn("Invalid CO index");
        return null;
    }

    coilValues[index] = boolVal;

    // Save to context
    context.set("coilValues", coilValues);

    // Send modbus write message for CO
    msg.payload = {
        value: coilValues,
        fc: 15,
        unitid: 1,
        address: 0,
        quantity: coilValues.length
    };
} else {
    node.warn("Unsupported type");
    return null;
}

return msg;

const baseTopicCO = "Modbus Device/CO";
const coilValues = msg.payload.slice(0, 10); // Limit to only the first 10 coils

coilValues.forEach((value, index) => {
