// Convert a 32-bit float to two 16-bit registers (Little Endian LSRF)
function floatToModbus(value) {
    let buffer = Buffer.alloc(4);
    buffer.writeFloatLE(value, 0); // Write as Little Endian float

    // Extract 16-bit registers in LSRF order
    let reg1 = buffer.readUInt16LE(0); // Least Significant Register (LSR)
    let reg2 = buffer.readUInt16LE(2); // Most Significant Register (MSR)

    return [reg1, reg2]; 
}

